mapper.update:
UPDATE USER_U
 SET U_ID=#{u_id}, U_PASSWORD=#{u_password}, U_EMAIL=#{u_email}, U_PHONE=#{u_phone}
  WHERE U_ID=#{u_id} AND U_AUTH=#{u_auth}

UPDATE USER_U
 SET U_ENABLE='N'
  WHERE U_ID=#{u_id}

mapper.select:
SELECT U_ID, U_PASSWORD, U_NAME, U_EMAIL, U_PHONE, U_ENABLE, U_AUTH, F_NAME
 FROM USER_U
 	WHERE U_AUTH = #{u_auth}

SELECT U_ID, U_PASSWORD, U_NAME, U_EMAIL, U_PHONE, U_ENABLE, U_AUTH, F_NAME
 FROM USER_U
  WHERE U_ID=#{u_id}

mapper.update:
UPDATE USER_U
 SET U_AUTH=#{u_auth}
  WHERE U_ID=#{u_id}
